$screen-xs: 0;
$screen-sm: 480px;
$screen-md: 768px;
$screen-lg: 992px;
$screen-xl: 1200px;
$screen-xxl: 1800px;

$main-color: #7290a1;
$main-title-color: #45555f;
$accent-color: #0077ff;
$strong-color: #2d9cdb;
$main-bg-color: #ffffff;

// styles
html {
  font-family: Gilroy;
  font-size: 16px;
  line-height: 140%;
  color: $main-color;
}

body {
  min-width: 320px;
}

.page {
  .page__header {
    height: 64px;
    display: flex;
    align-items: center;

    @media screen and (max-width: $screen-xl) {
      height: 56px;
    }
  }
}

.header {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background: $main-bg-color;
  box-shadow: 0px 4px 28px rgba(65, 109, 131, 0.05);

  @media screen and (max-width: $screen-xl) {
    justify-content: flex-start;
    padding: 0 40px;
    height: 56px;
  }

  @media screen and (max-width: $screen-sm) {
    padding: 0 16px;
  }

  &__burger {
    display: none;
    cursor: pointer;

    @media screen and (max-width: $screen-xl) {
      display: block;
    }
  }

  &__logo {
    cursor: pointer;

    @media screen and (max-width: $screen-xl) {
      margin-left: 24px;

      svg {
        width: 86px;
        height: 20px;
      }
    }

    @media screen and (max-width: $screen-sm) {
      margin-left: 16px;
    }
  }

  &__nav {
    @media screen and (max-width: $screen-xl) {
      display: block;
      position: absolute;
      z-index: 2;
      top: 58px;
      left: 0;
      background: $main-bg-color;
      box-shadow: 0px 4px 28px rgba(65, 109, 131, 0.05);
    }
  }

  &__download-link {
    margin-left: 40px;
    cursor: pointer;
    color: $accent-color;

    @media screen and (max-width: $screen-xl) {
      margin-left: auto;
    }

    @media screen and (max-width: $screen-sm) {
      font-size: 14/16 * 1rem;
    }
  }
}

nav {
  margin-left: 108px;

  @media screen and (max-width: $screen-xl) {
    margin-left: 0;
    display: flex;
    flex-direction: column;
    min-width: 255px;
  }
  @media screen and (max-width: $screen-sm) {
    padding-bottom: 16px;
  }

  &__item {
    margin-left: 40px;
    cursor: pointer;

    @media screen and (max-width: $screen-xl) {
      margin-bottom: 24px;
    }

    @media screen and (max-width: $screen-sm) {
      margin-left: 16px;
    }

    &:first-child {
      margin-left: 0;

      @media screen and (max-width: $screen-xl) {
        margin-left: 40px;
      }

      @media screen and (max-width: $screen-sm) {
        margin-left: 16px;
      }
    }
  }
}

.hidden {
  display: none;
}

.section1 {
  position: relative;
  background-image: url("./images/loon-image-original.jpg");
  width: 100%;
  min-height: calc(100vh - 64px);
  background-size: cover;
