    padding: 96px 16px 0;
  }

  &__promo {
    max-width: 763px;
    // position: absolute;
    left: calc(378 / 1920 * 100vw);
    top: calc(162 / 900 * 100vh);
  }

  &__title {
    font-size: 48/16 * 1rem;
    line-height: 120%;
    letter-spacing: 0.2px;
    color: $main-title-color;
    font-weight: bold;
    margin-bottom: 121px;

    @media screen and (max-width: $screen-xl) and (min-width: $screen-sm + 1px) {
      font-size: 40/16 * 1rem;
    }

    @media screen and (max-width: $screen-sm) and (min-width: $screen-xs) {
      font-size: 28/16 * 1rem;
      margin-bottom: 80px;
    }

    span {
      color: $strong-color;
      display: block;
    }

    span:last-child {
      font-size: 22/16 * 1rem;
      line-height: 130%;
      letter-spacing: 0.2px;
      color: $strong-color;
      @media screen and (max-width: $screen-sm) and (min-width: $screen-xs) {
        font-size: 1rem;
        margin-top: 16px;
      }
    }
  }

  &__advantage {
    @media screen and (max-width: $screen-xl) {
      max-width: 256px;
      margin-bottom: 108px;
    }

    ul {
      // list-style-type: circle;
      color: $main-title-color;
    }
    li {
      font-weight: 600;
      font-size: 18/16 * 1rem;
      line-height: 140%;
      letter-spacing: 0.2px;
      display: flex;
      align-items: center;
      @media screen and (max-width: $screen-xl) {
        margin-bottom: 18px;
        align-items: flex-start;
      }

      &::before {
        content: "â—¯";
        font-size: 10/16 * 1rem;
        color: $strong-color;
        padding-right: 12px;
        font-weight: 900;
      }
    }
  }

  &__form {
    // position: absolute;
    // top: calc(162 / 900 * 100vh);
    // right: calc(378 / 1920 * 100vw);
    margin-left: 29px;
    box-sizing: border-box;
    min-width: 372px;
    width: 372px;
    background: #ffffff;
    box-shadow: 0px 4px 70px rgba(58, 83, 113, 0.1);
    border-radius: 16px;
    padding: 40px 46px;

    @media screen and (max-width: $screen-xl) and (min-width: $screen-sm + 1px) {
      position: absolute;
      bottom: 0;
      right: 40px;
      align-self: flex-end;
    }

    @media screen and (max-width: $screen-sm) and (min-width: $screen-xs) {
      position: relative;
      margin: auto;
      min-width: 100%;
      width: 100%;
      padding: 40px 16px;
    }
  }
